def linkcode_resolve(domain, info):
    import importlib
    if domain != 'py':
        return None
    if not info['module']:
        return None
    filename = info['module'].replace('.', '/')
    suffix='py'
    module_suffix = importlib.import_module(info['module']).__file__[-2:]
    if module_suffix  == 'so':
        suffix = 'pyx'
    return "https://github.com/erdc-cm/proteus/tree/master/%s.%s" % (filename, suffix)

